CREATE TABLE "ASSET_MUTATIONS" -- Biến động tài sản
(
  "ID" NUMBER(18,0) GENERATED BY DEFAULT ON NULL AS IDENTITY (START WITH 1 INCREMENT BY 1) PRIMARY KEY,
  "ASSET_ID" NUMBER(18,0) REFERENCES "ASSETS"("ID") NOT NULL, -- ID tài sản
  "ASSET_CODE" VARCHAR2(250 BYTE) NOT NULL, -- Mã tài sản
  "ASSET_MUTATION_TYPE_ID" NUMBER(10,0) REFERENCES "ASSET_MUTATION_TYPES"("ID") NOT NULL,
  "SYNC_USER_ID" NUMBER(10,0) REFERENCES "SYNC_USERS"("ID") NOT NULL, -- Tài khoản đồng bộ
  "SYNC_DATE" DATE DEFAULT SYSDATE NOT NULL, -- Ngày đồng bộ
  
  "DELETED_DATE" DATE NULL, -- Ngày xóa dữ liệu
  "DELETED_USER_ID" NUMBER(10,0) REFERENCES "USERS"("ID") NULL, -- Người xóa dữ liệu
  
  -- Thông tin biến động
  "INCREASEMENT_CAUSE_ID" NUMBER(10,0) NULL REFERENCES "INCREASEMENT_CAUSES"("ID"), -- Lý do tăng
  "DECREASEMENT_CAUSE_ID" NUMBER(10,0) NULL REFERENCES "DECREASEMENT_CAUSES"("ID"), -- Lý do giảm
  "CHANGE_REASON" VARCHAR2(4000 BYTE), -- Lý do thay đổi
  "TRANSFER_UNIT_ID" NUMBER(10,0) REFERENCES "UNITS"("ID"), -- ID đơn vị điều chuyển
  "TRANSFER_UNIT_CODE" VARCHAR2(250 BYTE) REFERENCES "UNITS"("CODE"), -- Mã đơn vị điều chuyển
  "TRANSFER_UNIT_NAME" VARCHAR2(500 BYTE), -- Tên đơn vị ngoài ngành điều chuyển
  "DECREASEMENT_DATE" DATE, -- Ngày giảm

  /************* Thông tin tài sản, copy từ bảng tài sản *************/
  
  -- Thông tin chung
  "SYNC_SOURCE_ID" VARCHAR2(250 BYTE) NOT NULL, -- Mã tài sản cũ
  "NAME" VARCHAR2(4000 BYTE) NOT NULL, -- Ten tài sản
  "DESCRIPTION" VARCHAR2(4000 BYTE) NULL, -- Mô tả tài sản
