  "ORIGINAL_VALUE_SOURCE_STATE_BUDGET" NUMBER(18,0), -- Nguồn NSNN
  "ORIGINAL_VALUE_SOURCE_ODA" NUMBER(18,0), -- Nguồn ODA
  "ORIGINAL_VALUE_SOURCE_AID" NUMBER(18,0), -- Nguồn viện trợ
  "ORIGINAL_VALUE_SOURCE_BORROW" NUMBER(18,0), -- Nguồn vay
  "ORIGINAL_VALUE_SOURCE_OTHER" NUMBER(18,0), -- Nguồn khác
