CREATE TABLE "ASSET_INVENTORY_REPORTS" -- Biên bản kiểm kê tài sản
(
  "ID" NUMBER(10,0) GENERATED BY DEFAULT ON NULL AS IDENTITY (START WITH 1 INCREMENT BY 1) PRIMARY KEY,
  "UNIT_ID" NUMBER(10,0) REFERENCES "UNITS"("ID") NULL, -- Đơn vị kiểm kê
  "NAME" VARCHAR2(500 BYTE) NOT NULL, -- Tên phiếu kiểm kê
  "DESCRIPTION" VARCHAR2(500 BYTE) NOT NULL, -- Mô tả phiếu kiểm kê
  "INVENTORY_FORM" NUMBER(10,0) NOT NULL, -- Hình thức kiểm kê. 1: Kiểm kê thường niên. 2: Kiểm kê đột xuất. 3: Kiểm kê dể xử lý. 4: Kiểm kê khác
  "INVENTORY_YEAR" NUMBER(10,0) NOT NULL, -- Năm kiểm kê
  "CREATED_DATE" DATE NOT NULL, -- Ngày lập biên bản
  "INVENTORY_DATE" DATE, -- Ngày kiểm kê
  "APPROVED_DATE" DATE, -- Ngày duyệt
  "APPROVER" VARCHAR2(4000 BYTE), -- Người duyệt
  "STATUS" NUMBER(10,0), -- Trạng thái biên bản. 1: Chờ duyệt. 2: Đã duyệt đang xử lý. 3: Đã xử lý xong. 4: Từ chối. 5: Đã hủy
  "SYNC_USER_ID" NUMBER(10,0) REFERENCES "SYNC_USERS"("ID") NOT NULL, -- Tài khoản đồng bộ
  "SYNC_DATE" DATE DEFAULT SYSDATE NOT NULL -- Ngày đồng bộ
);

CREATE TABLE "ASSET_INVENTORY_DETAILS" -- Tài sản kiểm kê
(
  "ID" NUMBER(18,0) GENERATED BY DEFAULT ON NULL AS IDENTITY (START WITH 1 INCREMENT BY 1) PRIMARY KEY,
  "ASSET_INVENTORY_REPORT_ID" NUMBER(10,0) REFERENCES "ASSET_INVENTORY_REPORTS"("ID") NOT NULL,
  "ASSET_ID" NUMBER(18,0) REFERENCES "ASSETS"("ID") NOT NULL,
  "ASSET_NAME" VARCHAR2(4000 BYTE) NOT NULL,
  "HANDLING_RESULT" NUMBER(10,0) NOT NULL, -- Xử lý tài sản sau kiểm kê
  "NOTE" VARCHAR2(4000 BYTE) NULL,
  "SYNC_USER_ID" NUMBER(10,0) REFERENCES "SYNC_USERS"("ID") NOT NULL, -- Tài khoản đồng bộ
  "SYNC_DATE" DATE DEFAULT SYSDATE NOT NULL -- Ngày đồng bộ
);

CREATE TABLE "ASSET_DEPRECIATIONS" -- Khấu hao tài sản
(
  "ID" NUMBER(18,0) GENERATED BY DEFAULT ON NULL AS IDENTITY (START WITH 1 INCREMENT BY 1) PRIMARY KEY,
  "ASSET_ID" NUMBER(18,0) REFERENCES "ASSETS"("ID") NOT NULL, -- Mã tài sản
  "YEAR" NUMBER(10,0) NOT NULL, -- Năm khấu hao
  "MONTH" NUMBER(10,0) NOT NULL, -- Tháng khấu hao
  "DEPRECIATION_VALUE" NUMBER(18,0) NOT NULL, -- Giá trị khấu hao
  "ACCUMULATED_DEPRECIATION_VALUE" NUMBER(18,0) NOT NULL, -- Giá trị khấu hao tích lũy
  "ORIGINAL_VALUE" NUMBER(18,0) NOT NULL, -- Nguyên giá
  "RESIDUAL_VALUE" NUMBER(18,0) NOT NULL, -- Giá trị còn lại
  "SYNC_USER_ID" NUMBER(10,0) REFERENCES "SYNC_USERS"("ID") NOT NULL, -- Tài khoản đồng bộ
  "SYNC_DATE" DATE DEFAULT SYSDATE NOT NULL -- Ngày đồng bộ
  -- OTHER INFO
);

CREATE TABLE "ASSET_AMORTIZATIONS" -- Hao mòn tài sản
(
  "ID" NUMBER(18,0) GENERATED BY DEFAULT ON NULL AS IDENTITY (START WITH 1 INCREMENT BY 1) PRIMARY KEY,
  "ASSET_ID" NUMBER(18,0) REFERENCES "ASSETS"("ID") NOT NULL, -- Mã tài sản
  "YEAR" NUMBER(10,0) NOT NULL, -- Năm hao mòn
  "AMORTIZATION_VALUE" NUMBER(18,0) NOT NULL, -- Giá trị hao mòn
  "ACCUMULATED_AMORTIZATION_VALUE" NUMBER(18,0) NOT NULL, -- Giá trị hao mòn tích lũy
  "ORIGINAL_VALUE" NUMBER(18,0) NOT NULL, -- Nguyên giá
  "RESIDUAL_VALUE" NUMBER(18,0) NOT NULL, -- Giá trị còn lại
  "SYNC_USER_ID" NUMBER(10,0) REFERENCES "SYNC_USERS"("ID") NOT NULL, -- Tài khoản đồng bộ
  "SYNC_DATE" DATE DEFAULT SYSDATE NOT NULL -- Ngày đồng bộ
  -- OTHER INFO
);

CREATE TABLE "ASSET_EXPLOITATIONS" -- Khai thác tài sản
(
  "ID" NUMBER(18,0) GENERATED BY DEFAULT ON NULL AS IDENTITY (START WITH 1 INCREMENT BY 1) PRIMARY KEY,
  "ASSET_ID" NUMBER(18,0) REFERENCES "ASSETS"("ID") NOT NULL, -- Mã tài sản
  "UNIT_ID" NUMBER(10,0) REFERENCES "UNITS"("ID") NULL, -- Đơn vị sở hữu tài sản
  "EXPLOITATION_TYPE" NUMBER(10,0) NOT NULL, -- Hình thức khai thác. 1: Cho thuê. 2: Tham gia kinh doanh. 3: Mang liên doanh, liên kết
  "START_DATE" NUMBER(10,0) NOT NULL, -- Ngày bắt đầu khai thác
  "END_DATE" NUMBER(10,0) NOT NULL, -- Ngày kết thúc khai thác
  "DECISION_CODE" VARCHAR2(50 BYTE) NOT NULL, -- Số quyết định
  "ISSUED_DATE" DATE NOT NULL, -- Ngày quyết định
  "APPROVER" VARCHAR2(250 BYTE) NOT NULL, -- Người quyết định
  "PRICE" NUMBER(18,0) NOT NULL, -- Đơn giá
  "EXPLOITED_TIME" NUMBER(10,0) NOT NULL, -- Thời gian khai thác
  "EXPLOITED_VALUE" NUMBER(18,0) NOT NULL, -- Số tiền thu được
  "CONTRACT" VARCHAR2(4000 BYTE) NULL, -- Hợp đồng liên quan
  "PARTNER" VARCHAR2(4000 BYTE) NULL, -- Đối tác/Đơn vị thuê
  "NOTES" CLOB NULL, -- Ghi chú
  "SYNC_USER_ID" NUMBER(10,0) REFERENCES "SYNC_USERS"("ID") NOT NULL, -- Tài khoản đồng bộ
  "SYNC_DATE" DATE DEFAULT SYSDATE NOT NULL -- Ngày đồng bộ
);




